(()=>{"use strict";new class{constructor(){this.fromCurrency=document.getElementById("currency-one"),this.toCurrency=document.getElementById("currency-two"),this.fromAmount=document.getElementById("amount-one"),this.toAmount=document.getElementById("amount-two"),this.swapButton=document.getElementById("swap"),this.showRateArea=document.getElementById("rate"),this.showExchangedResult(),[this.fromAmount,this.fromCurrency,this.toCurrency].forEach((t=>t.addEventListener("change",this.showExchangedResult.bind(this)))),this.swapButton.addEventListener("click",this.swap.bind(this))}showExchangedResult(){fetch("https://open.exchangerate-api.com/v6/latest").then((t=>t.json())).then((t=>this.convert(t.rates)))}convert(t){const e=t[this.fromCurrency.value]/t[this.toCurrency.value];this.showRateArea.innerText=`1 ${this.fromCurrency.value} = ${1/e} ${this.toCurrency.value}`;const n=parseInt(this.fromAmount.value)/e;this.toAmount.value=(Math.floor(100*n)/100).toString()}swap(){const t=this.fromCurrency.value;this.fromCurrency.value=this.toCurrency.value,this.toCurrency.value=t,this.showExchangedResult()}}})();